package Lec1;

public class Arithmetic_Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 98;
		int y = 17;
		int a = x + y;
		System.out.println(a);
		a = x - y;
		System.out.println(a);
		int b = x * y;
		System.out.println(b);
		int c = x / y;
		System.out.println(c);
		c = x % y;
		System.out.println(c);

	}

}
